//Tests.js------Show chosen Test

import React, { useEffect } from 'react';
import { useSelector } from 'react-redux';
import  { ShowCurrentTest } from "../showCurrentTest/ShowCurrentTest.js";
import '../tests/Tests.css';
import '../scrollToTopBtn/scrollToTopBtn.css';

let WHY_botton_visibility   = null;

let congrFinishTest         = null;

let current_question        = null;

let count_questions_test    = null;

let correct_answer          = null;


export function Tests() {

    let showCurrentTestWork = null;


    let showHideTest        = useSelector((store) => store.showHideTest);

    let currentTest         = useSelector((store) => store.currentTest);

    congrFinishTest         = useSelector((store) => store.congrFinishTest);

    current_question        = useSelector((store) => store.current_question);

    count_questions_test    = useSelector((store) => store.count_questions_test);

    WHY_botton_visibility   = useSelector((store) => store.WHY_botton_visibility);

    correct_answer          = useSelector((store) => store.correct_answer);



    let nameCurrentTest     = "TEST" + (currentTest+1);

    let incorrectAnswerTxt = (count_questions_test - correct_answer) + ' incorrect answers';

    let greenRectangle = <div id='correct_answer'></div>;

    let correctAnswerTxt = correct_answer + ' correct answers';

    let userResults = ( <><div id="correct_answer"></div> 
        <span> {correct_answer_txt}</span> 
        <div id='incorrect_answer'></div>  
        <span> {incorrect_answer_txt}</span> 
        </>
    );


//    if(WHY_botton_visibility[0] == {visibility: "visible"})
//        congrFinishTest = 'Congratulate! You have complited the test. If you would like to check any of your answers, you must click on the appropriate WHY button.';

    if(current_question === count_questions_test) 
        congrFinishTest = (<div>Congratulate! You have complited the test. Your results are as follows: {userResults} 

        If you would like to check any of your answers, you must click on the appropriate WHY button.</div>);        

      //  congrFinishTest = userResults;
//console.log('current_question=',current_question);        
//console.log('count_questions_test=',count_questions_test);        
//console.log('congrFinishTest=',congrFinishTest);

    if(showHideTest === 'showHideTest') 
        showCurrentTestWork = <div> 
            <div className='topicSection'> 
                {nameCurrentTest}, level B1 
                <br />
                Choose the correct option for each sentence
                <hr />
            </div> 
            <ShowCurrentTest /> 
            <div id='congrFinishTest'>{congrFinishTest}</div>
            <button id='footerBtn'><a href="#London_bgr">UP</a></button>
        </div>
    else showCurrentTestWork = null;

    return <section id="tests_wrapper">

        {showCurrentTestWork}

    </section>
}

