//Tests.js------Show chosen Test

import React, { useEffect } from 'react';
import { useSelector } from 'react-redux';
import  { ShowCurrentTest } from "../showCurrentTest/ShowCurrentTest.js";
import '../scrollToTopBtn/scrollToTopBtn.css';

export function Tests() {

    let showHideTest = useSelector((store) => store.showHideTest);

    let showCurrentTestWork = null;

//    let script = document.createElement('script');

//    script.src = '../scrollToTopBtn/scrollToTopBtn.js';

//    document.head.append(script);

//    script.onload = scriptScrollToTopBtn();

    useEffect(() => {

        //Get the button
        let scrollToTopBtn = document.getElementById('scrollToTopBtn');
        console.log('scrollToTopBtn =',scrollToTopBtn);

        let rootElement = document.documentElement;

        function handleScroll(){
            //Do answering on scroll
            let scrollTotal = rootElement.scrollHeight -
                rootElement.clientHeight;
                if(rootElement.scrolling / scrollTotal > 0.0) {
                    //Show button
                    scrollToTopBtn.classList.add('showBtn');
                } else {
                    //Hide button
                    scrollToTopBtn.classList.remove('showBtn');
                }
        }//end of handleScroll

        function scrollToTop(){
            //scroll to top logic
            rootElement.scrollTo({
                top: 0,
                behavior: 'smooth'
            });
        }//end of scrollToTop


        //When the user clicks on the button, scroll to the top of the document
        function topFunction(){
            document.body.scrollTop = 0;
            document.documentElement.scrollTop = 0;
        }//end of topFunction

        scrollToTopBtn.addEventListener('click', scrollToTop);
        document.addEventListener('scroll',handleScroll);

    });//useEffect

/*        

        //When the user scroll down for 20px from the top of the document, show the button
